/* >= 375px */
@media only screen and (min-width: 375px) {
  /* 1rem = 375px */
  html {
    font-size : 375px;
  }
}
@media only screen and (min-width: 360px) {
  html {
    font-size : 360px;
  }
}

/* 
  在 375 设计稿下 logo 宽度为 100px
  375 - (100/375)rem = 100px
  360 - (100/375)rem = 96px
*/
@function px2rem($val) {
    @return $val / 375 + rem;
}
.logo {
    width : px2rem(100);
}

